mapper.select:
SELECT *, round( 6378.137 * 2 * ASIN( SQRT( POW(SIN((#{req.latitude} * PI() / 180 - latitude * PI() / 180) / 2), 2) + COS(#{req.latitude} * PI() / 180) * COS(#{req.latitude} * PI() / 180) * POW( SIN((#{req.longitude} * PI() / 180 - longitude * PI() / 180) / 2), 2) ) ) * 1000) as distance FROM product_info where sale_flag = 'true' product_info.product_name like concat('%',#{req.productName},'%')
product_info.product_type =#{req.productType}
ORDER BY distance ASC

mapper.update:
update product_info
url=#{url}, product_name=#{productName}, limit_time=#{limitTime}, total_limit=#{totalLimit}, return_point=#{returnPoint}, total_left=#{totalLeft}, recommend=#{recommend}, product_type=#{productType}, update_time=#{updateTime}, longitude=#{longitude}, latitude=#{latitude}, address=#{address}, member_return_point=#{memberReturnPoint}, least_cost=#{leastCost}, price=#{price}, rules=#{rules}, product_pic_url=#{productPicUrl},
WHERE product_id=#{productId}
